import { AppLoadContext } from 'react-router';

/**
 *
 * Utility function to get env variables across Node and Edge runtimes.
 *
 * @param name
 * @returns string
 */
declare const getEnvVariable: (name: string, context: AppLoadContext | undefined) => string;
declare const getPublicEnvVariables: (context: AppLoadContext | undefined) => {
    publishableKey: string;
    domain: string;
    isSatellite: boolean;
    proxyUrl: string;
    signInUrl: string;
    signUpUrl: string;
    clerkJsUrl: string;
    clerkJsVariant: "" | "headless" | undefined;
    clerkJsVersion: string;
    telemetryDisabled: boolean;
    telemetryDebug: boolean;
    signInForceRedirectUrl: string;
    signUpForceRedirectUrl: string;
    signInFallbackRedirectUrl: string;
    signUpFallbackRedirectUrl: string;
    afterSignInUrl: string;
    afterSignUpUrl: string;
};

export { getEnvVariable, getPublicEnvVariables };
